import React, { Component } from 'react';
import {Pagination, Container, Divider, Grid} from 'semantic-ui-react'; 
import PostCard from '../components/PostCard.js'

class ImageIndex extends Component {

    state ={
        loading: true,
        imageIndex: []
    }

    componentDidMount(){
        fetch(`https://localhost:3000/images`)
        .then(res => res.json())
        .then(this.initialState)
    }

    initialState = (resData) => {
        this.setState({
            // loading finished so update state to false
            loading: false,
            imageIndex: resData

        })
    }

    handlePage = (e, { activePage }) => {
        let gotopage = { activePage }
        let pagenum = gotopage.activePage
        let pagestring = pagenum.toString()
        this.setState({
        loading: true
        })
        const url = `https://localhost:3000/images/?page=` + pagestring

        fetch(url)
        .then(res => res.json())
        .then(this.initialState)
    }

    render() {
        console.log(this.state)
        return (
            <div>
                <Container>
                <Divider hidden />
                <Container textAlign='center'>
                <Pagination  onPageChange={this.handlePage} size='mini' siblingRange="6" defaultActivePage={this.state.imageIndex.page} totalPages={this.state.imageIndex.pages} />
                </Container>
                    <Divider hidden />
                <Grid relaxed columns={5}>
                {/* { this.state.imageIndex.map(image => {
                    return (<Grid.Column>
                    <PostCard key={image.id} image={image}/>
                    </Grid.Column>)
                }) } */}
                </Grid>

                <Divider hidden />

                <Container textAlign='center'>
                <Pagination  onPageChange={this.handlePage} size='mini' siblingRange="6" defaultActivePage={this.state.imageIndex.page} totalPages={this.state.imageIndex.pages} />
                </Container>
                <Divider hidden />

            </Container>
            </div>
        );
    }
}

export default ImageIndex;
