import React, { Component } from 'react';
import {Pagination, Container, Divider, Grid, Dimmer, Loader} from 'semantic-ui-react'; 
import PostCard from '../components/PostCard.js'

class ImageIndex extends Component {

    state = {
        loading: true,
        imageIndex: [],
        activePage:1
    }

    componentDidMount(){
        fetch(`http://localhost:3000/images`)
        .then(res => res.json())
        .then(initialState)
    }

    initialState = (resData) => {
        console.log(resData)
        this.setState({
            // loading finished so update state to false
            loading: false,
            imageIndex: resData,
            activePage: resData.page
        })
    }

    handlePage = (e, { activePage }) => {
        console.log(activePage)
        let gotopage = { activePage }
        let pagenum = gotopage.activePage
        let pagestring = pagenum.toString()
        console.log(pagestring)
        this.setState({
        loading: true
        })
        const url = `http://localhost:3000/images/?page=` + pagestring

        fetch(url)
        .then(res => res.json())
        .then(this.state)
    }

    render() {
        console.log(this.state)

        if(this.state.loading){
            return(
                <Container>
                <Dimmer active inverted>
                  <Loader>Loading Images...</Loader>
                </Dimmer>
              </Container>
            )
        }

        return (
            <Container>
                <Divider hidden />
                <Container textAlign='center'>

                <Pagination  onPageChange={this.handlePage} size='mini' siblingRange="6" defaultActivePage={this.state.imageIndex.page} totalPages={this.state.imageIndex.pages} />
                </Container>
                    <Divider hidden />
                <Grid relaxed columns={2}>
                { this.state.imageIndex.images.map(image => {
                    return (<Grid.Column>
                    <PostCard key={image.id} image={image}/>
                    </Grid.Column>)
                }) }
                </Grid>

                <Divider hidden />

                <Container textAlign='center'>
                <Pagination  onPageChange={this.handlePage} size='mini' siblingRange="6" defaultActivePage={this.state.imageIndex.page} totalPages={this.state.imageIndex.pages} />
                </Container>
                <Divider hidden />

            </Container>
        );
    }
}

export default ImageIndex;
